import React, { useEffect, useState } from "react";
import axios from "../api/axios";
import { useParams } from "react-router-dom";
import { Typography, Card, CardContent, Box, Button } from "@mui/material";

const PropertyDetails = () => {
  const { id } = useParams();
  const [property, setProperty] = useState(null);
  const [error, setError] = useState("");
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    axios.get(`/properties/${id}`)
      .then(res => setProperty(res.data.property))
      .catch(() => setError("Ошибка загрузки объявления"));

    axios.get(`/properties/${id}/reviews`)
      .then(res => setReviews(res.data.reviews))
      .catch(() => setReviews([]));
  }, [id]);

  if (error) return <Typography color="error">{error}</Typography>;
  if (!property) return <Typography>Загрузка...</Typography>;

  return (
    <Box mt={2}>
      <Card>
        <CardContent>
          <Typography variant="h5">{property.title}</Typography>
          <Typography>{property.city}, {property.address}</Typography>
          <Typography>Цена: {property.price_per_night}₽/ночь</Typography>
          <Typography>Описание: {property.description}</Typography>
          <Typography>Вместимость: {property.max_guests} гостей</Typography>
          <Typography>Спальни: {property.bedrooms}, Ванные: {property.bathrooms}</Typography>
          <Typography>Можно с питомцами: {property.pets_allowed ? "Да" : "Нет"}</Typography>
          {/* Здесь можно добавить кнопку "Забронировать" */}
          <Button variant="contained" color="primary" sx={{ mt: 2 }}>
            Забронировать
          </Button>
        </CardContent>
      </Card>
      <Box mt={4}>
        <Typography variant="h6">Отзывы:</Typography>
        {reviews.length === 0 && <Typography>Пока нет отзывов</Typography>}
        {reviews.map(review => (
          <Card key={review.id} sx={{ my: 1 }}>
            <CardContent>
              <Typography><b>{review.first_name} {review.last_name}</b> - Оценка: {review.rating}/5</Typography>
              <Typography>{review.comment}</Typography>
              {review.improvement_suggestions && (
                <Typography color="textSecondary">Предложения: {review.improvement_suggestions}</Typography>
              )}
            </CardContent>
          </Card>
        ))}
      </Box>
    </Box>
  );
};

export default PropertyDetails;
